sys_script_include.script:
var GetGroupMembers = Class.create();
GetGroupMembers.prototype = {
	initialize: function() {
	},

	getUsers: function(){

		var arr = [];
		var gr = new GlideRecord("sys_user_grmember");
		gr.addQuery("group.name", "Tenant");
		gr.query();
		while(gr.next()) {
			arr.push(gr.getValue('user'));
		}
		return arr.toString();

	},

	type: 'GetGroupMembers'
};
